mapper.select:
SELECT *
FROM member
WHERE id=#{id} AND pwd=#{pwd}

mapper.update:
UPDATE member
	SET pwd = #{pwd} , addr = #{addr}, name = #{name}, 
	phone = #{phone}, email = #{email} , age = #{age} 
WHERE id = #{id}

UPDATE member
	SET addr = #{addr}
WHERE id = #{id}

mapper.select:
SELECT *
FROM member
WHERE id = #{id} AND pwd = #{pwd}
